import { render } from "@testing-library/react";
import React, {useState, useEffect} from "react";
import CardList from "../components/CardList";
import Hand from "../components/Hand";
import './DeckContainer.css';

const DeckContainer = () => {

    const [deck, setDeck] = useState([])
    const [hand, setHand] = useState([])

    useEffect(() => {
        getDeck();
    }, [])


    const getDeck = function(){
        fetch('https://deckofcardsapi.com/api/deck/new/draw/?count=52')
        .then(res => res.json())
        .then(data => setDeck(data.cards))
    }

    // const addToHand = function(card){
    //     let newHand = [...hand, card];
    //     let uniqueNewHand = Array.from(new Set(newHand));
    //     setHand(uniqueNewHand);
    // }

    // const removeFromDeck = function(card){
    //     let newDeck = deck
    //     newDeck.splice(card,1)
    //     setDeck(newDeck);
    // }

    const moveFromDeckToHand = function(card){
        let newHand = [...hand, card];
        let uniqueNewHand = Array.from(new Set(newHand));
        let newDeck = deck
        setHand(uniqueNewHand);
        newDeck.splice(1, 0)
        setDeck(newDeck);
    }

    return (
        <div id="deck">            
            <p>Card List:</p>
            <CardList deck={deck} moveFromDeckToHand={moveFromDeckToHand}/>
            <p>Hand:</p>
            <Hand hand={hand} />
        </div>
    )
}

export default DeckContainer;
